function showPopup(type,message){var languageCode=document.documentElement.lang;message=encodeURIComponent(message);const xhr=new XMLHttpRequest;xhr.open("GET",`/?/control/popup/get/show&languageCode=${languageCode}&type=${type}&message=${message}`);xhr.send();xhr.onload=function(){if(xhr.status===200){document.body.insertAdjacentHTML("beforeend",xhr.responseText)}}}