var selectedCargo=0;var selectedInvoice=0;var radios=document.querySelectorAll('.address-card input[type="radio"]');radios.forEach(function(radio){radio.addEventListener("change",function(){if(this.name==="cargoAddressID"){selectedCargo=this.value}else if(this.name==="invoiceAddressID"){selectedInvoice=this.value}document.querySelectorAll(".address-card").forEach(function(card){card.classList.remove("selected-cargo","selected-invoice","selected-both")});if(selectedCargo){document.querySelector("#address-"+selectedCargo).classList.add("selected-cargo")}if(selectedInvoice){document.querySelector("#address-"+selectedInvoice).classList.add("selected-invoice")}if(selectedCargo&&selectedInvoice&&selectedCargo===selectedInvoice){document.querySelector("#address-"+selectedCargo).classList.add("selected-both")}})});