function resetTurnstile(){const turnstileContainerElement=document.getElementById("turnstile-container");if(typeof turnstile!=="undefined"&&turnstileContainerElement){try{console.log("Turnstile widget'ı sıfırlanıyor (#turnstile-container).");turnstile.reset(turnstileContainerElement)}catch(e){console.error("Turnstile sıfırlanırken hata oluştu:",e)}}}document.getElementById("contactForm").addEventListener("submit",function(e){e.preventDefault();var form=e.target;if(form.namesurname.value===""||form.email.value===""||form.phone.value===""||form.message.value===""){showPopup("error","Please fill in all fields.");return}var formData=new FormData(form);var languageCode=document.documentElement.lang;var csrfToken=document.getElementById("csrf_token-contact-form").value;var cfTurnStile=document.getElementById("cf-token-contact-form").value;formData.append("languageCode",languageCode);formData.append("cf-turnstile-response",cfTurnStile);formData.append("csrfToken",csrfToken);var xhr=new XMLHttpRequest;xhr.open("POST","/?/control/form/post/contactForm",true);xhr.send(formData);xhr.onload=function(){if(xhr.status===200){var response=JSON.parse(xhr.responseText);if(response.status==="success"){showPopup("success",response.message);form.reset()}else{showPopup("error",response.message)}}resetTurnstile()}});