var languageCode=document.documentElement.lang;var userIcon=document.querySelector(".svg-icon-container.member");var asideUser=document.querySelector(".aside-left-user");if(userIcon){userIcon.addEventListener("click",function(){event.preventDefault();asideUser.classList.add("active")})}var closeIcon=document.querySelector(".aside-left-user-close");if(closeIcon){closeIcon.addEventListener("click",function(){asideUser.classList.remove("active")})}var showAsideLeft=document.querySelector("#show-aside-left");if(showAsideLeft){showAsideLeft.addEventListener("click",function(){var asideLeftUser=document.querySelector(".aside-left-user");if(asideLeftUser.classList.contains("active")){asideUser.classList.remove("active");showAsideLeft.innerHTML='<label class="arrow-label">></label>'}else{asideUser.classList.add("active");showAsideLeft.innerHTML='<label class="arrow-label"><</label>'}})}if(document.querySelector(".svg-icon-container.search")){document.querySelector(".svg-icon-container.search").addEventListener("click",function(event){event.preventDefault();var searchIcon=document.querySelector(".product-search-container");searchIcon.classList.add("mobile-search-active")})}if(document.querySelector(".close-search")){document.querySelector(".close-search").addEventListener("click",function(event){event.preventDefault();var searchIcon=document.querySelector(".product-search-container");searchIcon.classList.remove("mobile-search-active")})}function removeTrailingZeros(value){console.log("removeTrailingZeros value: "+value);return value.toString().replace(/\.?0+$/,"")}function showPopup(type,message){message=encodeURIComponent(message);const xhr=new XMLHttpRequest;xhr.open("GET",`/?/control/popup/get/show&languageCode=${languageCode}&type=${type}&message=${message}`);xhr.send();xhr.onload=function(){if(xhr.status===200){document.body.insertAdjacentHTML("beforeend",xhr.responseText)}}}