window.openPayment=function(evt,paymentMethod){var i,tabcontent,tablinks;tabcontent=document.getElementsByClassName("tabcontent");for(i=0;i<tabcontent.length;i++){tabcontent[i].style.display="none"}tablinks=document.getElementsByClassName("tablinks");for(i=0;i<tablinks.length;i++){tablinks[i].className=tablinks[i].className.replace(" active","")}document.getElementById(paymentMethod).style.display="block";evt.currentTarget.className+=" active"};if(document.getElementById("payment-bank-button")){document.getElementById("payment-bank-button").addEventListener("click",function(e){e.preventDefault();var link=document.getElementById("payment-bank-button").getAttribute("href");console.log(link);document.getElementById("payment-bank-button").classList.add("disabled");var xhr=new XMLHttpRequest;xhr.open("GET",link,true);xhr.send();xhr.onload=function(){if(xhr.status===200){console.log(xhr.responseText);var response=JSON.parse(xhr.responseText);if(response.error){showPopupForPayment("error",response.message);document.getElementById("payment-bank-button").classList.remove("disabled")}else{showPopupForPayment("success",response.message);setTimeout(function(){window.location.href="/?/control/member/get/orders"},2e3)}}else{showPopupForPayment("error","Bir hata oluştu. Lütfen tekrar deneyin.");document.getElementById("payment-bank-button").classList.remove("disabled")}}})}function showPopupForPayment(type,message){var languageCode=document.documentElement.lang;const xhr=new XMLHttpRequest;xhr.open("GET",`/?/control/popup/get/show&type=${type}&message=${message}&&languageCode=${languageCode}&autoClose=false`);xhr.send();xhr.onload=function(){if(xhr.status===200){document.body.insertAdjacentHTML("beforeend",xhr.responseText);loadScript("/Public/JS/popup.min.js?v=4")}}}