--
-- Tablo için tablo yapısı `sayfa`
--

CREATE TABLE `sayfa` (
                         `sayfaid` int(11) NOT NULL AUTO_INCREMENT,
                         `benzersizid` char(20) NOT NULL,
                         `sayfatariholustur` datetime(6) NOT NULL,
                         `sayfatarihguncel` datetime(6) NOT NULL,
                         `sayfatip` tinyint(1) NOT NULL,
                         `sayfaad` varchar(255) DEFAULT NULL,
                         `sayfaicerik` longtext DEFAULT NULL,
                         `sayfalink` varchar(255) DEFAULT NULL,
                         `sayfasira` tinyint(4) NOT NULL DEFAULT 0,
                         `sayfaaktif` tinyint(1) NOT NULL DEFAULT 1,
                         `sayfasil` tinyint(1) NOT NULL DEFAULT 0,
                         `sayfahit` int(11) DEFAULT 0,
                         PRIMARY KEY (`sayfaid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


--
-- Tablo döküm verisi `sayfa`
--

INSERT INTO `sayfa` (`sayfaid`, `benzersizid`, `sayfatariholustur`, `sayfatarihguncel`, `sayfatip`, `sayfaad`, `sayfaicerik`, `sayfalink`, `sayfasira`, `sayfaaktif`, `sayfasil`, `sayfahit`) VALUES
                                                                                                                                                                                                  (1, 'AJBCV2RGK3U5HS8ZWQND', '2019-03-01 23:34:14.000000', '2022-02-22 15:13:04.000000', 17, 'Üyelik', '', '', 2, 1, 0, 0),
                                                                                                                                                                                                  (2, 'AU64YSX7CTBMV8N2J9RF', '2019-03-01 16:55:14.000000', '2018-04-23 19:39:10.000000', 10, 'Üyelik Sözleşmesi', '&lt;p&gt;1. Taraflar&lt;br /&gt; a) [sitedomain] &amp;nbsp;internet sitesinin faaliyetlerini y&amp;uuml;r&amp;uuml;ten [firmamahalle] [firmaadres] [firmasemt] [firmailce]/[firmasehir]/[firmaulke] &amp;nbsp;adresinde mukim [firmaunvan]. (Bundan b&amp;ouml;yle &amp;ldquo;[sitedomain]&amp;rdquo; olarak anılacaktır).&lt;br /&gt; b) [sitedomain] &amp;nbsp;internet sitesine &amp;uuml;ye olan internet kullanıcısı (&amp;quot;&amp;Uuml;ye&amp;quot;)&lt;/p&gt;  &lt;p&gt;2. S&amp;ouml;zleşmenin Konusu&lt;br /&gt; İşbu S&amp;ouml;zleşme&amp;rsquo;nin konusu [sitedomain]&amp;rsquo;nın sahip olduğu internet sitesi [sitedomain] &amp;nbsp;&amp;lsquo;dan &amp;uuml;yenin faydalanma şartlarının belirlenmesidir.&lt;/p&gt;  &lt;p&gt;3. Tarafların Hak ve Y&amp;uuml;k&amp;uuml;ml&amp;uuml;l&amp;uuml;kleri&lt;br /&gt; 3.1. &amp;Uuml;ye, [sitedomain] &amp;nbsp;internet sitesine &amp;uuml;ye olurken verdiği kişisel ve diğer sair bilgilerin kanunlar &amp;ouml;n&amp;uuml;nde doğru olduğunu, [sitedomain]&amp;#39;nın bu bilgilerin ger&amp;ccedil;eğe aykırılığı nedeniyle uğrayacağı t&amp;uuml;m zararları aynen ve derhal tazmin edeceğini beyan ve taahh&amp;uuml;t eder.&lt;br /&gt; 3.2. &amp;Uuml;ye, [sitedomain] tarafından kendisine verilmiş olan şifreyi başka kişi ya da kuruluşlara veremez, &amp;uuml;yenin s&amp;ouml;zkonusu şifreyi kullanma hakkı bizzat kendisine aittir. Bu sebeple doğabilecek t&amp;uuml;m sorumluluk ile &amp;uuml;&amp;ccedil;&amp;uuml;nc&amp;uuml; kişiler veya yetkili merciler tarafından [sitedomain]&amp;#39;ya karşı ileri s&amp;uuml;r&amp;uuml;lebilecek t&amp;uuml;m iddia ve taleplere karşı, [sitedomain]&amp;#39;nın s&amp;ouml;zkonusu izinsiz kullanımdan kaynaklanan her t&amp;uuml;rl&amp;uuml; tazminat ve sair talep hakkı saklıdır.&lt;br /&gt; 3.3. &amp;Uuml;ye [sitedomain] &amp;nbsp;internet sitesini kullanırken yasal mevzuat h&amp;uuml;k&amp;uuml;mlerine riayet etmeyi ve bunları ihlal etmemeyi baştan kabul ve taahh&amp;uuml;t eder. Aksi takdirde, doğacak t&amp;uuml;m hukuki ve cezai y&amp;uuml;k&amp;uuml;ml&amp;uuml;l&amp;uuml;kler tamamen ve m&amp;uuml;nhasıran &amp;uuml;yeyi bağlayacaktır.&lt;br /&gt; 3.4. &amp;Uuml;ye, [sitedomain] &amp;nbsp;internet sitesini hi&amp;ccedil;bir şekilde kamu d&amp;uuml;zenini bozucu, genel ahlaka aykırı, başkalarını rahatsız ve taciz edici şekilde, yasalara aykırı bir ama&amp;ccedil; i&amp;ccedil;in, başkalarının fikri ve telif haklarına tecav&amp;uuml;z edecek şekilde kullanamaz. Ayrıca, &amp;uuml;ye başkalarının hizmetleri kullanmasını &amp;ouml;nleyici veya zorlaştırıcı faaliyet (spam, virus, truva atı, vb.) ve işlemlerde bulunamaz.&lt;br /&gt; 3.5. [sitedomain] &amp;nbsp;internet sitesinde &amp;uuml;yeler tarafından beyan edilen, yazılan, kullanılan fikir ve d&amp;uuml;ş&amp;uuml;nceler, tamamen &amp;uuml;yelerin kendi kişisel g&amp;ouml;r&amp;uuml;şleridir ve g&amp;ouml;r&amp;uuml;ş sahibini bağlar. Bu g&amp;ouml;r&amp;uuml;ş ve d&amp;uuml;ş&amp;uuml;ncelerin [sitedomain]&amp;#39;yla hi&amp;ccedil;bir ilgi ve bağlantısı yoktur. [sitedomain]&amp;#39;nın &amp;uuml;yenin beyan edeceği fikir ve g&amp;ouml;r&amp;uuml;şler nedeniyle &amp;uuml;&amp;ccedil;&amp;uuml;nc&amp;uuml; kişilerin uğrayabileceği zararlardan ve &amp;uuml;&amp;ccedil;&amp;uuml;nc&amp;uuml; kişilerin beyan edeceği fikir ve g&amp;ouml;r&amp;uuml;şler nedeniyle &amp;uuml;yenin uğrayabileceği zararlardan dolayı herhangi bir sorumluluğu bulunmamaktadır.&lt;br /&gt; 3.6. [sitedomain], &amp;uuml;ye verilerinin yetkisiz kişilerce okunmasından ve &amp;uuml;ye yazılım ve verilerine gelebilecek zararlardan dolayı sorumlu olmayacaktır. &amp;Uuml;ye, [sitedomain] &amp;nbsp;internet sitesinin kullanılmasından dolayı uğrayabileceği herhangi bir zarar y&amp;uuml;z&amp;uuml;nden [sitedomain]&amp;#39;dan tazminat talep etmemeyi peşinen kabul etmiştir.&lt;br /&gt; 3.7. &amp;Uuml;ye, diğer internet kullanıcılarının yazılımlarına ve verilerine izinsiz olarak ulaşmamayı veya bunları kullanmamayı kabul etmiştir. Aksi takdirde, bundan doğacak hukuki ve cezai sorumluluklar tamamen &amp;uuml;yeye aittir.&lt;br /&gt; 3.8. İşbu &amp;uuml;yelik s&amp;ouml;zleşmesi i&amp;ccedil;erisinde sayılan maddelerden bir ya da birka&amp;ccedil;ını ihlal eden &amp;uuml;ye işbu ihlal nedeniyle cezai ve hukuki olarak şahsen sorumlu olup, [sitedomain]&amp;rsquo;yı bu ihlallerin hukuki ve cezai sonu&amp;ccedil;larından ari tutacaktır. Ayrıca; işbu ihlal nedeniyle, olayın hukuk alanına intikal ettirilmesi halinde, [sitedomain]&amp;#39;nın &amp;uuml;yeye karşı &amp;uuml;yelik s&amp;ouml;zleşmesinee uyulmamasından dolayı tazminat talebinde bulunma hakkı saklıdır.&lt;br /&gt; 3.9. [sitedomain]&amp;#39;nın her zaman tek taraflı olarak gerektiğinde &amp;uuml;yenin &amp;uuml;yeliğini silme, m&amp;uuml;şteriye ait dosya, belge ve bilgileri silme hakkı vardır. &amp;Uuml;ye işbu tasarrufu &amp;ouml;nceden kabul eder. Bu durumda, [sitedomain]&amp;#39;nın hi&amp;ccedil;bir sorumluluğu yoktur.&lt;br /&gt; 3.10. [sitedomain] &amp;nbsp;internet sitesi yazılım ve tasarımı [sitedomain] m&amp;uuml;lkiyetinde olup, bunlara ilişkin telif hakkı ve/veya diğer fikri m&amp;uuml;lkiyet hakları ilgili kanunlarca korunmakta olup, bunlar &amp;uuml;ye tarafından izinsiz kullanılamaz, iktisap edilemez ve değiştirilemez. Bu web sitesinde adı ge&amp;ccedil;en başkaca şirketler ve &amp;uuml;r&amp;uuml;nleri sahiplerinin ticari markalarıdır ve ayrıca fikri m&amp;uuml;lkiyet hakları kapsamında korunmaktadır.&lt;br /&gt; 3.11. [sitedomain] tarafından [sitedomain] &amp;nbsp;internet sitesinin iyileştirilmesi, geliştirilmesine y&amp;ouml;nelik olarak ve/veya yasal mevzuat &amp;ccedil;er&amp;ccedil;evesinde siteye erişmek i&amp;ccedil;in kullanılan İnternet servis sağlayıcısının adı ve Internet Protokol (IP) adresi, Siteye erişilen tarih ve saat, sitede bulunulan sırada erişilen sayfalar ve siteye doğrudan bağlanılmasını sağlayan Web sitesinin Internet adresi gibi birtakım bilgiler toplanabilir.&lt;br /&gt; 3.12. [sitedomain] kullanıcılarına daha iyi hizmet sunmak, &amp;uuml;r&amp;uuml;nlerini ve hizmetlerini iyileştirmek, sitenin kullanımını kolaylaştırmak i&amp;ccedil;in kullanımını kullanıcılarının &amp;ouml;zel tercihlerine ve ilgi alanlarına y&amp;ouml;nelik &amp;ccedil;alışmalarda &amp;uuml;yelerin kişisel bilgilerini kullanabilir. [sitedomain], &amp;uuml;yenin [sitedomain] &amp;nbsp;internet sitesi &amp;uuml;zerinde yaptığı hareketlerin kaydını bulundurma hakkını saklı tutar.&lt;br /&gt; 3.13. [sitedomain]&amp;rsquo;ya &amp;uuml;ye olan kişi, y&amp;uuml;r&amp;uuml;rl&amp;uuml;kte bulunan ve/veya y&amp;uuml;r&amp;uuml;rl&amp;uuml;ğe alınacak uygulamalar kapsamında [sitedomain] ve [firmaunvan] iştiraki olan t&amp;uuml;m şirketler tarafından kendisine &amp;uuml;r&amp;uuml;n ve hizmet tanıtımları, reklamlar, kampanyalar, avantajlar, anketler ve diğer m&amp;uuml;şteri memnuniyeti uygulamaları sunulmasına izin verdiğini beyan ve kabul eder. &amp;Uuml;ye, [sitedomain]&amp;rsquo;ya &amp;uuml;ye olurken ve/veya başka yollarla ge&amp;ccedil;mişte vermiş olduğu ve/veya gelecekte vereceği kişisel ve alışveriş bilgilerinin ve alışveriş ve/veya t&amp;uuml;ketici davranış bilgilerinin yukarıdaki ama&amp;ccedil;larla toplanmasına, [firmaunvan] iştiraki olan t&amp;uuml;m şirketler ile paylaşılmasına, [sitedomain] ve [firmaunvan] iştiraki olan t&amp;uuml;m şirketler tarafından kullanılmasına ve arşivlenmesine izin verdiğini beyan ve kabul eder. &amp;Uuml;ye aksini bildirmediği s&amp;uuml;rece &amp;uuml;yeliği sona erdiğinde de verilerin toplanmasına, [firmaunvan] iştiraki olan t&amp;uuml;m şirketler ile paylaşılmasına, [sitedomain] ve [firmaunvan] iştiraki olan t&amp;uuml;m şirketler tarafından kullanılmasına ve arşivlenmesine izin verdiğini beyan ve kabul eder. &amp;Uuml;ye aksini bildirmediği s&amp;uuml;rece [sitedomain] ve [firmaunvan] iştiraki olan t&amp;uuml;m şirketlerin kendisi ile internet, telefon, SMS, vb iletişim kanalları kullanarak irtibata ge&amp;ccedil;mesine izin verdiğini beyan ve kabul eder. &amp;Uuml;ye yukarıda bahsi ge&amp;ccedil;en bilgilerin toplanması, paylaşılması, kullanılması, arşivlenmesi ve kendisine erişilmesi nedeniyle doğrudan ve/veya dolaylı maddi ve/veya manevi menfi ve/veya m&amp;uuml;sbet, velhasıl herhangi bir zarara uğradığı konusunda talepte bulunmayacağını ve [sitedomain] ve [firmaunvan] iştiraki olan şirketleri sorumlu tutmayacağını beyan ve kabul eder. &amp;Uuml;ye, kişisel verileri ile ilgili detaylı bilgiye https://[sitedomain] / adresinden ulaşabilir.&lt;br /&gt; 3.14. [sitedomain], &amp;uuml;yenin kişisel bilgilerini yasal bir zorunluluk olarak istendiğinde veya (a) yasal gereklere uygun hareket etmek veya [sitedomain]&amp;rsquo;ya tebliğ edilen yasal işlemlere uymak; (b) [sitedomain] ve [sitedomain] web sitesi ailesinin haklarını ve m&amp;uuml;lkiyetini korumak ve savunmak i&amp;ccedil;in gerekli olduğuna iyi niyetle kanaat getirdiği hallerde a&amp;ccedil;ıklayabilir.&lt;br /&gt; 3.15. [sitedomain] web sitesinin virus ve benzeri ama&amp;ccedil;lı yazılımlardan arındırılmış olması i&amp;ccedil;in mevcut imkanlar dahilinde tedbir alınmıştır. Bunun yanında nihai g&amp;uuml;venliğin sağlanması i&amp;ccedil;in kullanıcının, kendi virus koruma sistemini tedarik etmesi ve gerekli korunmayı sağlaması gerekmektedir. Bu bağlamda &amp;uuml;ye [sitedomain] web sitesi&amp;#39;ne girmesiyle, kendi yazılım ve işletim sistemlerinde oluşabilecek t&amp;uuml;m hata ve bunların doğrudan ya da dolaylı sonu&amp;ccedil;larından kendisinin sorumlu olduğunu kabul etmiş sayılır.&lt;br /&gt; 3.16. [sitedomain], sitenin i&amp;ccedil;eriğini dilediği zaman değiştirme, kullanıcılara sağlanan herhangi bir hizmeti değiştirme ya da sona erdirme veya [sitedomain] web sitesi&amp;#39;nde kayıtlı kullanıcı bilgi ve verilerini silme hakkını saklı tutar.&lt;br /&gt; 3.17. [sitedomain], &amp;uuml;yelik s&amp;ouml;zleşmesinin koşullarını hi&amp;ccedil;bir şekil ve surette &amp;ouml;n ihbara ve/veya ihtara gerek kalmaksızın her zaman değiştirebilir, g&amp;uuml;ncelleyebilir veya iptal edebilir. Değiştirilen, g&amp;uuml;ncellenen ya da y&amp;uuml;r&amp;uuml;rl&amp;uuml;kten kaldırılan her h&amp;uuml;k&amp;uuml;m , yayın tarihinde t&amp;uuml;m &amp;uuml;yeler bakımından h&amp;uuml;k&amp;uuml;m ifade edecektir.&lt;br /&gt; 3.18. Taraflar, [sitedomain]&amp;#39;ya ait t&amp;uuml;m bilgisayar kayıtlarının tek ve ger&amp;ccedil;ek m&amp;uuml;nhasır delil olarak, HUMK madde 287&amp;#39;ye uygun şekilde esas alınacağını ve s&amp;ouml;z konusu kayıtların bir delil s&amp;ouml;zleşmesi teşkil ettiği hususunu kabul ve beyan eder.&lt;br /&gt; 3.19. [sitedomain], iş bu &amp;uuml;yelik s&amp;ouml;zleşmesi uyarınca, &amp;uuml;yelerinin kendisinde kayıtlı elektronik posta adreslerine bilgilendirme mailleri ve cep telefonlarına bilgilendirme SMS&amp;rsquo;leri g&amp;ouml;nderme yetkisine sahip olmakla beraber, &amp;uuml;ye işbu &amp;uuml;yelik s&amp;ouml;zleşmesini onaylamasıyla beraber bilgilendirme maillerinin elektronik posta adresine ve bilgilendirme SMS&amp;rsquo;lerinin cep telefonuna g&amp;ouml;nderilmesini kabul etmiş sayılacaktır. &amp;Uuml;ye mail ve/veya SMS almaktan vazge&amp;ccedil;mek istemesi durumunda &amp;ldquo;Benim Sayfam&amp;rdquo; b&amp;ouml;l&amp;uuml;m&amp;uuml;ndeki &amp;ldquo;Tercihlerim&amp;rdquo; kısmından mail ve/veya SMS g&amp;ouml;nderim iptal işlemini ger&amp;ccedil;ekleştirebilecektir.&lt;/p&gt;  &lt;p&gt;4. S&amp;ouml;zleşmenin Feshi&lt;br /&gt; İşbu s&amp;ouml;zleşme &amp;uuml;yenin &amp;uuml;yeliğini iptal etmesi veya [sitedomain] tarafından &amp;uuml;yeliğinin iptal edilmesine kadar y&amp;uuml;r&amp;uuml;rl&amp;uuml;kte kalacaktır. [sitedomain] &amp;uuml;yenin &amp;uuml;yelik s&amp;ouml;zleşmesinin herhangi bir h&amp;uuml;km&amp;uuml;n&amp;uuml; ihlal etmesi durumunda &amp;uuml;yenin &amp;uuml;yeliğini iptal ederek s&amp;ouml;zleşmeyi tek taraflı olarak feshedebilecektir.&lt;/p&gt;  &lt;p&gt;5. İhtilaflerin Halli&lt;br /&gt; İşbu s&amp;ouml;zleşmeye ilişkin ihtilaflerde İstanbul Mahkemeleri ve İcra Daireleri yetkilidir.&lt;/p&gt;  &lt;p&gt;6. Y&amp;uuml;r&amp;uuml;rl&amp;uuml;k&lt;br /&gt; &amp;Uuml;yenin, &amp;uuml;yelik kaydı yapması &amp;uuml;yenin &amp;uuml;yelik s&amp;ouml;zleşmesinde yer alan t&amp;uuml;m maddeleri okuduğu ve &amp;uuml;yelik s&amp;ouml;zleşmesinde yer alan maddeleri kabul ettiği anlamına gelir. İşbu S&amp;ouml;zleşme &amp;uuml;yenin &amp;uuml;ye olması anında akdedilmiş ve karşılıklı olarak y&amp;uuml;r&amp;uuml;rl&amp;uuml;l&amp;uuml;ğe girmiştir.&lt;/p&gt;', '/uyelik-sozlesmesi', 16, 1, 0, 0),
                                                                                                                                                                                                  (3, 'DWKYR6S52QT4HE73CNVZ', '2019-03-01 12:00:55.000000', '2018-03-30 14:47:13.000000', 13, 'Çerez Politikası', '&lt;p&gt;[firmaunvan]. (&amp;ldquo;[sitedomain]&amp;rdquo; olarak, kullanıcılarımızın hizmetlerimizden g&amp;uuml;venli ve eksiksiz şekilde faydalanmalarını sağlamak amacıyla sitemizi kullanan kişilerin gizliliğini korumak i&amp;ccedil;in &amp;ccedil;alışıyoruz.&lt;/p&gt;  &lt;p&gt;&amp;Ccedil;oğu web sitesinde olduğu gibi, [sitedomain].com (&amp;ldquo;Site&amp;rdquo;) ile mobil uygulamanın (hepsi birlikte &amp;ldquo;Platform&amp;rdquo; olarak anılacaktır) ziyaret&amp;ccedil;ilere kişisel i&amp;ccedil;erik ve reklamlar g&amp;ouml;stermek, site i&amp;ccedil;inde analitik faaliyetler ger&amp;ccedil;ekleştirmek ve ziyaret&amp;ccedil;i kullanım alışkanlıklarını takip etmek amacıyla &amp;Ccedil;erezler kullanılmaktadır.&lt;/p&gt;  &lt;p&gt;İşbu &amp;Ccedil;erez Politakası [sitedomain].com Gizlilik Politikası&amp;rsquo;nın ayrılmaz bir par&amp;ccedil;asıdır.&lt;br /&gt; [sitedomain], bu &amp;Ccedil;erez Politikası&amp;rsquo;nı (&amp;ldquo;Politika&amp;rdquo;) Site&amp;rsquo;de hangi &amp;Ccedil;erezlerin kullanıldığını ve kullanıcıların bu konudaki tercihlerini nasıl y&amp;ouml;netebileceğini a&amp;ccedil;ıklamak amacıyla hazırlamıştır. [sitedomain] tarafından kişisel verilerinizin işlenmesine ilişkin daha detaylı bilgi i&amp;ccedil;in [sitedomain].com Gizlilik Politikası&amp;rsquo;nı incelemenizi tavsiye ederiz.&lt;/p&gt;  &lt;p&gt;&lt;strong&gt;&amp;Ccedil;erez (&amp;ldquo;Cookie&amp;rdquo;) Nedir?&lt;/strong&gt;&lt;br /&gt; &amp;Ccedil;erezler, ziyaret ettiğiniz internet siteleri tarafından tarayıcılar aracılığıyla cihazınıza veya ağ sunucusuna depolanan k&amp;uuml;&amp;ccedil;&amp;uuml;k metin dosyalarıdır. &amp;Ccedil;erezler, ziyaret ettiğiniz web sitesiyle ilişkili sunucular tarafından oluşturulurlar. B&amp;ouml;ylelikle ziyaret&amp;ccedil;i aynı siteyi ziyaret ettiğinde sunucu bunu anlayabilir.&lt;br /&gt; &amp;Ccedil;erezler, ziyaret&amp;ccedil;ilere ilişkin isim, cinsiyet veya adres gibi kişisel verileri i&amp;ccedil;ermezler. &amp;Ccedil;erezler konusunda daha detaylı bilgi i&amp;ccedil;in www.aboutcookies.org ve www.allaboutcookies.org adreslerini ziyaret edebilirisiniz.&lt;/p&gt;  &lt;p&gt;&lt;strong&gt;Hangi &amp;Ccedil;erezler Kullanılmaktadır?&lt;/strong&gt;&lt;br /&gt; &amp;Ccedil;erezler, sahipleri, kullanım &amp;ouml;m&amp;uuml;rleri ve kullanım ama&amp;ccedil;ları a&amp;ccedil;ısında kategorize edilebilir:&lt;br /&gt; &amp;bull; &amp;Ccedil;erezi yerleştiren tarafa g&amp;ouml;re, Platform &amp;ccedil;erezleri ve &amp;uuml;&amp;ccedil;&amp;uuml;nc&amp;uuml; taraf &amp;Ccedil;erezler kullanılmaktadır. Platform &amp;ccedil;erezleri, [sitedomain] tarafından oluşturulurken, &amp;uuml;&amp;ccedil;&amp;uuml;nc&amp;uuml; taraf &amp;ccedil;erezlerini [sitedomain] ile iş birlikteliği olan farklı firmalar y&amp;ouml;netmektedir.&lt;br /&gt; &amp;bull; Aktif olduğu s&amp;uuml;reye g&amp;ouml;re, oturum &amp;ccedil;erezleri ve kalıcı &amp;ccedil;erezler kullanılmaktadır. Oturum &amp;ccedil;erezleri ziyaret&amp;ccedil;inin Platform&amp;rsquo;u terk etmesiyle birlikte silinirken, kalıcı &amp;ccedil;erezler ise kullanım alanına bağlı olarak &amp;ccedil;eşitli s&amp;uuml;relerle ziyaret&amp;ccedil;ilerin cihazlarında kalabilmektedir.&lt;br /&gt; &amp;bull; Kullanım ama&amp;ccedil;larına g&amp;ouml;re, Platform&amp;rsquo;da teknik &amp;ccedil;erezler, doğrulama &amp;ccedil;erezleri, hedefleme/reklam &amp;ccedil;erezleri, kişiselleştirme &amp;ccedil;erezleri ve analitik &amp;ccedil;erezler kullanılmaktadır.&lt;/p&gt;  &lt;p&gt;&lt;strong&gt;Neden &amp;Ccedil;erezler Kullanılmaktadır?&lt;/strong&gt;&lt;br /&gt; Platform&amp;rsquo;da, &amp;Ccedil;erezler aşağıdaki ama&amp;ccedil;lar kapsamında kullanılmaktadır:&lt;br /&gt; &amp;bull; Platform&amp;rsquo;un &amp;ccedil;alışması i&amp;ccedil;in gerekli temel fonksiyonları ger&amp;ccedil;ekleştirmek. &amp;Ouml;rneğin, [sitedomain] &amp;uuml;yelerinin, &amp;uuml;r&amp;uuml;n sepetindeki &amp;uuml;r&amp;uuml;nlerin ziyaretleri s&amp;uuml;resince kaybolmaması. Oturum a&amp;ccedil;an &amp;uuml;yelerin Platform&amp;rsquo;da farklı sayfaları ziyaret ederken tekrar şifre girmelerine gerek kalmaması.&lt;br /&gt; &amp;bull; Platform&amp;rsquo;u analiz etmek ve Platform&amp;rsquo;un performansını arttırmak. &amp;Ouml;rneğin, Platform&amp;rsquo;un &amp;uuml;zerinde &amp;ccedil;alıştığı farklı sunucuların entegrasyonu, Platform&amp;rsquo;u ziyaret edenlerin sayısının tespit edilmesi ve buna g&amp;ouml;re performans ayarlarının yapılması ya da ziyaret&amp;ccedil;ilerin aradıklarını bulmalarının kolaylaştırılması.&lt;br /&gt; &amp;bull; Platform&amp;rsquo;un işlevselliğini arttırmak ve kullanım kolaylığı sağlamak. &amp;Ouml;rneğin, Platform &amp;uuml;zerinden &amp;uuml;&amp;ccedil;&amp;uuml;nc&amp;uuml; taraf sosyal medya mecralarına paylaşımda bulunmak, Platform&amp;rsquo;u ziyaret eden ziyaret&amp;ccedil;inin daha sonraki ziyaretinde kullanıcı adı bilgisinin ya da arama sorgularının hatırlanması&lt;br /&gt; &amp;bull; Kişiselleştirme, hedefleme ve reklamcılık faaliyeti ger&amp;ccedil;ekleştirmek. &amp;Ouml;rneğin, ziyaret&amp;ccedil;ilerin g&amp;ouml;r&amp;uuml;nt&amp;uuml;ledikleri sayfa ve &amp;uuml;r&amp;uuml;nler &amp;uuml;zerinden ziyaret&amp;ccedil;ilerin ilgi alanlarıyla bağlantılı reklam g&amp;ouml;sterilmesi.&lt;/p&gt;  &lt;p&gt;&lt;strong&gt;&amp;Ccedil;erez Tercihlerinizi Nasıl Y&amp;ouml;netebilirsiniz?&lt;/strong&gt;&lt;br /&gt; [sitedomain], kullanıcıların kendilerine ait kişisel veriler &amp;uuml;zerindeki tercihlerini kullanabilmelerini son derece &amp;ouml;nemsemektedir. Bununla birlikte, Site&amp;rsquo;nin &amp;ccedil;alışması i&amp;ccedil;in zorunlu olan bazı &amp;Ccedil;erezler konusunda tercih y&amp;ouml;netimi m&amp;uuml;mk&amp;uuml;n olmamaktadır. Ayrıca bazı &amp;Ccedil;erezlerin kapatılması halinde Site&amp;rsquo;nin &amp;ccedil;eşitli fonksiyonlarının &amp;ccedil;alışmayabileceğini hatırlatma isteriz.&lt;/p&gt;  &lt;p&gt;Platform&amp;rsquo;da kullanılan &amp;Ccedil;erezlere dair tercihlerin ne şekilde y&amp;ouml;netebileceğine dair bilgiler aşağıdaki gibidir:&lt;br /&gt; &amp;bull; Ziyaret&amp;ccedil;iler, Platform&amp;rsquo;u g&amp;ouml;r&amp;uuml;nt&amp;uuml;ledikleri tarayıcı ayarlarını değiştirerek &amp;ccedil;erezlere ilişkin tercihlerini kişiselleştirme imkanına sahiptir. Eğer kullanılmakta olan tarayıcı bu imk&amp;acirc;nı sunmaktaysa, tarayıcı ayarları &amp;uuml;zerinden &amp;Ccedil;erezlere ilişkin tercihleri değiştirmek m&amp;uuml;mk&amp;uuml;nd&amp;uuml;r. B&amp;ouml;ylelikle, tarayıcının sunmuş olduğu imkanlara g&amp;ouml;re farklılık g&amp;ouml;sterebilmekle birlikte, veri sahiplerinin &amp;ccedil;erezlerin kullanılmasını engelleme, &amp;ccedil;erez kullanılmadan &amp;ouml;nce uyarı almayı tercih etme veya sadece bazı &amp;Ccedil;erezleri devre dışı bırakma ya da silme imkanları bulunmaktadır. Bu konudaki tercihler kullanılan tarayıcıya g&amp;ouml;re değişiklik g&amp;ouml;stermekle birlikte genel a&amp;ccedil;ıklamaya https://www.aboutcookies.org/ adresinden ulaşmak m&amp;uuml;mk&amp;uuml;nd&amp;uuml;r. &amp;Ccedil;erezlere ilişkin tercihlerin, ziyaret&amp;ccedil;inin Platform&amp;rsquo;a erişim sağladığı her bir cihaz &amp;ouml;zelinde ayrı ayrı yapılması gerekebilecektir.&lt;br /&gt; &amp;bull; Google Analytics tarafından y&amp;ouml;netilen &amp;Ccedil;erezleri kapatmak i&amp;ccedil;in &lt;a href=&quot;https://tools.google.com/dlpage/gaoptout&quot; target=&quot;_blank&quot;&gt;tıklayınız&lt;/a&gt;.&lt;br /&gt; &amp;bull; Google tarafından sağlanan kişiselleştirilmiş reklam deneyimini y&amp;ouml;netmek i&amp;ccedil;in &lt;a href=&quot;https://adssettings.google.com/authenticated&quot; target=&quot;_blank&quot;&gt;tıklayınız&lt;/a&gt;.&lt;br /&gt; &amp;bull; Bir&amp;ccedil;ok firmanın reklam faaliyetleri i&amp;ccedil;in kullandığı &amp;ccedil;erezler bakımından tercihler &lt;a href=&quot;https://www.youronlinechoices.com/tr/reklam-tercihleriniz&quot; target=&quot;_blank&quot;&gt;Your Online Choices&lt;/a&gt; &amp;uuml;zerinden y&amp;ouml;netilebilir.&lt;br /&gt; &amp;bull; Mobil cihazlar &amp;uuml;zerinden &amp;Ccedil;erezleri y&amp;ouml;netmek i&amp;ccedil;in mobil cihaza ait ayarlar men&amp;uuml;s&amp;uuml; kullanılabilir.&lt;/p&gt;  &lt;p&gt;&lt;strong&gt;Hangi Haklara Sahipsiniz?&lt;/strong&gt;&lt;br /&gt; 6698 Sayılı Kişisel Verilerin Korunması Kanunu&amp;rsquo;nun 11. maddesi uyarınca ziyaret&amp;ccedil;iler, [sitedomain]&amp;rsquo;ya başvurarak, kendileriyle ilgili,&lt;br /&gt; &amp;bull; Kişisel veri işlenip işlenmediğini &amp;ouml;ğrenme,&lt;br /&gt; &amp;bull; Kişisel verileri işlenmişse buna ilişkin bilgi talep etme,&lt;br /&gt; &amp;bull; Kişisel verilerin işlenme amacını ve bunların amacına uygun kullanılıp kullanılmadığını &amp;ouml;ğrenme,&lt;br /&gt; &amp;bull; Yurt i&amp;ccedil;inde veya yurt dışında kişisel verilerin aktarıldığı &amp;uuml;&amp;ccedil;&amp;uuml;nc&amp;uuml; kişileri bilme,&lt;br /&gt; &amp;bull; Kişisel verilerin eksik veya yanlış işlenmiş olması h&amp;acirc;linde bunların d&amp;uuml;zeltilmesini isteme ve bu kapsamda yapılan işlemin kişisel verilerin aktarıldığı &amp;uuml;&amp;ccedil;&amp;uuml;nc&amp;uuml; kişilere bildirilmesini isteme,&lt;br /&gt; &amp;bull; Kanun ve ilgili diğer kanun h&amp;uuml;k&amp;uuml;mlerine uygun olarak işlenmiş olmasına rağmen, işlenmesini gerektiren sebeplerin ortadan kalkması h&amp;acirc;linde kişisel verilerin silinmesini veya yok edilmesini isteme ve bu kapsamda yapılan işlemin kişisel verilerin aktarıldığı &amp;uuml;&amp;ccedil;&amp;uuml;nc&amp;uuml; kişilere bildirilmesini isteme,&lt;br /&gt; &amp;bull; İşlenen verilerin m&amp;uuml;nhasıran otomatik sistemler vasıtasıyla analiz edilmesi suretiyle kişinin kendisi aleyhine bir sonucun ortaya &amp;ccedil;ıkmasına itiraz etme,&lt;br /&gt; &amp;bull; Kişisel verilerin kanuna aykırı olarak işlenmesi sebebiyle zarara uğraması h&amp;acirc;linde zararın giderilmesini talep etme&lt;br /&gt; haklarına sahiptir&lt;/p&gt;  &lt;p&gt;&lt;strong&gt;Rıza ve Gizlilik Politikası&amp;rsquo;ndaki Değişiklikler&lt;/strong&gt;&lt;br /&gt; [sitedomain], Gizlilik Politikası (&amp;ldquo;Politika&amp;rdquo;) ile kullanıcılarına &amp;Ccedil;erez kullanımının kapsamı ve ama&amp;ccedil;ları hakkında detaylı a&amp;ccedil;ıklama sunmayı ve &amp;Ccedil;erez tercihleri konusunda kullanıcılarını bilgilendirmeyi hedeflemiştir. Bu bakımdan, Platform&amp;rsquo;da yer alan &amp;Ccedil;erez bilgilendirme uyarısının kapatılması ve Site&amp;rsquo;nin kullanılmaya devam edilmesi halinde &amp;Ccedil;erez kullanımına rıza verildiği kabul edilmektedir. Kullanıcıların &amp;Ccedil;erez tercihlerini değiştirme imk&amp;acirc;nı her zaman saklıdır.&lt;br /&gt; [sitedomain], Politika h&amp;uuml;k&amp;uuml;mlerini dilediği zaman değiştirebilir. G&amp;uuml;ncel Politika Platform&amp;rsquo;da yayınlandığı tarihte y&amp;uuml;r&amp;uuml;rl&amp;uuml;k kazanır.&lt;/p&gt;  &lt;p&gt;&amp;nbsp;&lt;/p&gt;', '', 15, 1, 0, 0),
                                                                                                                                                                                                  (4, 'KUA5SV67MRFCJT9Y2WHB', '2019-03-01 12:02:27.000000', '2023-03-23 11:16:17.000000', 14, 'İptal İade Değişim', '&lt;p&gt;İlgili yasal mevzuat gereği hiçbir sebep göstermeksizin ürünlerinizi teslim aldıktan sonra 10 gün içerisinde iade edebilisiniz.&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;Satın alınan ürün kullanılmamış, tahrip edilmemiş, paketi hasar görmemiş, aksesuarı ya da hediyesi olan bir ürün ise eksik olmaksızın iade edilmiş ve tekrar satılabilir olması gerekmektedir.&lt;br /&gt;\r\nİndirimdeki ve / veya kampanyalı ürünlerimizin iadesi yoktur. Sadece numara, vs. gereği değişimleri yapılır.&lt;br /&gt;\r\nİade edeceğiniz ürünün tarafımıza kargolanırken adınıza kesilmiş faturanın ürün ile birlikte tarafımıza gönderilmesi zorunludur - gereklidir. &amp;nbsp;Faturası gönderilmeyen ürünün iadesi &amp;nbsp;veya değişimi&amp;nbsp;gerçekleştirilmez.&lt;br /&gt;\r\nÜrün yukarıda belirtilen koşullar dahilinde iade işlemi başlatılıp, tarafımıza ulaştıktan sonra iadeninin şartlara uygunluğu kontrol edilecektir. Kontrol sırasında uygun bulunan iadeler için yapılan ödemenin şekline sadık kalınarak iade işlemi 2 iş günü içerisinde geçekleştirilir. Kredi kartı ile yapılan ödemelerde ürün bedeli, bankanıza bağlı olarak 1-10 gün içerisinde hesabınıza yansır.&lt;/p&gt;\r\n\r\n&lt;p&gt;Üründen kaynaklanan bir kusurdan dolayı yapacağınız iade ve değişim taleplerinde ilgili yasal mevzuat şartları geçerlidir. ürünün incelenmesinden sonra konuyla ilgili size bilgi vericeketir.&amp;nbsp;&lt;br /&gt;\r\nSiparişleriniz size teslim edilirken mutlaka kargo görevlisİnin yanında paketi açarak hasar olup olmadığına bakınız. hasar olması durumunda ürünü teslim almayınız ve kargo görevlisinden tutanak tutmasını isteyiniz.&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;İade Ve Değişim Adresi&amp;nbsp;&amp;nbsp;&lt;br /&gt;\r\n[sitedomain] – [firmaunvan]&lt;br /&gt;\r\n[firmaadres]&lt;br /&gt;\r\n[firmailce]/[firmasehir]/[firmaulke]&lt;/p&gt;\r\n\r\n&lt;p&gt;İade ve Değişim Avantajları&amp;nbsp;&lt;br /&gt;\r\nTüm iade ve Değişimlerinizde anlaşmalı kargo firmamız ( *** KARGO İSMİ *** ) ile gönderim gerçekleştirdiğiniz takdirde kargo masraflarını alıcı ödemeli olarak gönderebilirsiniz. Farklı kargolar ile gönderim gerçekleştirmeniz durumunda kargo ücreti size ait olur. İndirimli ürünlerimizde iade işlemi yapılmamaktadır.&lt;/p&gt;', '', 1, 1, 0, 0),
                                                                                                                                                                                                  (5, 'ME3BHKZ48Q5TN6PV9FSU', '2019-03-01 16:57:18.000000', '2018-03-29 16:57:18.000000', 9, 'Ödeme Kontrol', '', '', 3, 1, 0, 0),
                                                                                                                                                                                                  (6, 'RGA7TFHBVC42DQXZU8Y5', '2019-03-01 11:55:18.000000', '2023-03-13 14:53:27.000000', 12, 'Mesafeli Satış Sözleşmesi', '&lt;p&gt;MADDE 1 TARAFLAR&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\n1.1-SATICI&lt;/p&gt;\r\n\r\n&lt;p&gt;Ünvanı : [firmaunvan]&lt;/p&gt;\r\n\r\n&lt;p&gt;Adresi : [firmaadres] [firmailce]/[firmasehir]/[firmaulke]&lt;/p&gt;\r\n\r\n&lt;p&gt;Telefon : [firmatelefon]&lt;/p&gt;\r\n\r\n&lt;p&gt;Fax : [firmafaks]&lt;/p&gt;\r\n\r\n&lt;p&gt;Eposta adresi : [firmaeposta]&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\n1.2-ALICI&lt;/p&gt;\r\n\r\n&lt;p&gt;Adı/Soyadı/Ünvanı :&lt;/p&gt;\r\n\r\n&lt;p&gt;Adresi :&lt;/p&gt;\r\n\r\n&lt;p&gt;Telefon :&lt;/p&gt;\r\n\r\n&lt;p&gt;Eposta adresi :&lt;/p&gt;\r\n\r\n&lt;p&gt;MADDE 2 KONU&lt;/p&gt;\r\n\r\n&lt;p&gt;İşbu sözleşmenin konusu, ALICI\'nın SATICI\'ya ait [sitedomain]&amp;nbsp; internet sitesi üzerinden elektronik ortamda siparişini yaptığı aşağıda nitelikleri ve satış fiyatı belirtilen ürünün satışı ve teslimi ile ilgili olarak 4077 sayılı Tüketicilerin Korunması Hakkındaki Kanun ve 06.03.2011 Tarihli Mesafeli Sözleşmelere Dair Yönetmelik hükümleri gereğince tarafların hak ve yükümlülüklerinin belirlenmesidir.&lt;/p&gt;\r\n\r\n&lt;p&gt;MADDE 3 SATIŞA KONU ÜRÜNÜN ÖZELLİKLERİ&lt;/p&gt;\r\n\r\n&lt;p&gt;3.1-ÜRÜNLER&lt;/p&gt;\r\n\r\n&lt;p&gt;Mal ya da hizmetlerin temel özellikleri [sitedomain] adresinde yer almaktadır.&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;Listelenen ve sitede ilan edilen fiyatlar satış fiyatıdır. İlan edilen fiyatlar ve vaatler güncelleme yapılana ve değiştirilene kadar geçerlidir. Süreli olarak ilan edilen fiyatlar ise belirtilen süre sonuna kadar geçerlidir.&lt;/p&gt;\r\n\r\n&lt;p&gt;Sözleşme Konusu mal ya da hizmetin tüm vergileri satış fiyatına dahildir.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\n3.2-TESLİMAT ŞEKLİ VE PLANI&lt;/p&gt;\r\n\r\n&lt;p&gt;Teslimat Adresi :&lt;/p&gt;\r\n\r\n&lt;p&gt;Teslim Edilecek Kişi :&lt;/p&gt;\r\n\r\n&lt;p&gt;Fatura Bilgileri :&lt;/p&gt;\r\n\r\n&lt;p&gt;Ürün sevkiyatlarına ürün [firmaunvan]&amp;nbsp;tarafından tedarik edildikten sonraki en geç 3 işgünü içinde başlanacaktır. Siparişlerde ürün sevkiyat masrafı olan kargo ücreti ALICI tarafından ödenecektir.&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;3.3 GECİKME FAİZİ&lt;/p&gt;\r\n\r\n&lt;p&gt;Ödeme peşin ya da kredi kartı ile yapılacağı için SATICI tarafından gecikme faizi uygulanmaz.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\nMADDE 4 GENEL HÜKÜMLER&lt;/p&gt;\r\n\r\n&lt;p&gt;4.1) ALICI, [sitedomain] internet sitesinde sözleşme konusu ürünün temel nitelikleri, satış fiyatı ve ödeme şekli ile teslimata ilişkin ön bilgileri okuyup bilgi sahibi olduğunu ve elektronik ortamda gerekli teyidi verdiğini beyan eder. ALICI; bu Ön Bilgilendirmeyi elektronik ortamda teyit etmekle, mesafeli sözleşmelerin akdinden önce, SATICI tarafından ALICI\'ya verilmesi gereken adres, siparişi verilen ürünlere ait temel özellikler, ürünlerin vergiler dahil fiyatı, ödeme ve teslimat bilgilerini de doğru ve eksiksiz olarak edindiğini teyid etmiş olur.&lt;/p&gt;\r\n\r\n&lt;p&gt;4.2) Sözleşme konusu ürün, yasal 30 günlük süreyi aşmamak koşulu ile her bir ürün için ALICI\'nın yerleşim yerinin uzaklığına bağlı olarak internet sitesinde yer alan ön bilgiler içinde açıklanan süre içinde ALICI veya gösterdiği adresteki kişi/kuruluşa teslim edilir.&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;4.3) Kargo firmasının, ürünü ALICI’ya teslimi aşamasında karşılaşacağı her türlü sorun nedeniyle, siparişi verilen ürünün ALICI\'ya teslim edilememesinden dolayı SATICI sorumlu tutulamaz. Ürün sevkiyatında oluşan herhangi bir zarar için ALICI KARGO firmasına tutanak tutturmak zorundadır.&lt;/p&gt;\r\n\r\n&lt;p&gt;4.4) SATICI, sözleşme konusu ürünün sağlam, eksiksiz, siparişte belirtilen niteliklere uygun ve varsa garanti belgeleri ve kullanım kılavuzları ile teslim edilmesinden sorumludur.&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;4.5) SATICI, sözleşmeden doğan ifa yükümlülüğünün süresi dolmadan ALICI’yı bilgilendirmek ve açıkça onayını almak suretiyle eşit kalite ve fiyatta farklı bir ürün tedarik edebilir.&lt;/p&gt;\r\n\r\n&lt;p&gt;4.6) SATICI, sipariş konusu ürün veya hizmetin yerine getirilmesinin imkânsızlaşması halinde sözleşme konusu yükümlülüklerini yerine getiremezse, bu durumu, sözleşmeden doğan ifa yükümlülüğünün süresi dolmadan tüketiciye bildirir ve 10 iş günlük süre içinde toplam bedeli ALICI’ya iade eder.&lt;/p&gt;\r\n\r\n&lt;p&gt;4.7) Ürünün teslimatı için işbu Ön Bilgilendirme Formunun elektronik ortamda teyid edilmesi şarttır. Herhangi bir nedenle ürün bedeli ödenmez veya banka kayıtlarında iptal edilir ise, SATICI ürünün teslimi yükümlülüğünden kurtulmuş kabul edilir.&lt;/p&gt;\r\n\r\n&lt;p&gt;4.8) Ürünün tesliminden sonra ALICI\'ya ait kredi kartının ALICI\'nın kusurundan kaynaklanmayan bir şekilde yetkisiz kişilerce haksız veya hukuka aykırı olarak kullanılması nedeni ile ilgili banka veya finans kuruluşun ürün bedelini SATICI\'ya ödememesi halinde, ALICI kendisine teslim edilmiş olması kaydıyla ürünü 3 gün içinde SATICI\'ya iade etmek zorundadır. Bu takdirde nakliye giderleri ALICI\'ya aittir.&lt;/p&gt;\r\n\r\n&lt;p&gt;4.9) SATICI mücbir sebepler veya nakliyeyi engelleyen hava muhalefeti, ulaşımın kesilmesi gibi olağanüstü durumlar nedeni ile sözleşme konusu ürünü süresi içinde teslim edemez ise, durumu ALICI\'ya bildirmekle yükümlüdür. Bu takdirde ALICI siparişin iptal edilmesini, sözleşme konusu ürünün varsa emsali ile değiştirilmesini ve/veya teslimat süresinin engelleyici durumun ortadan kalkmasına kadar ertelenmesi haklarından birini kullanabilir. ALICI\'nın siparişi iptal etmesi halinde ödediği tutar 10 gün içinde kendisine nakden ve defaten ödenir. ALICI’nın kredi kartı ile yaptığı ödemelerde ise, ürün tutarı, siparişin ALICI tarafından iptal edilmesinden sonra 7 gün içerisinde ilgili bankaya iade edilir. Bu tutarın bankaya iadesinden sonra ALICI hesaplarına yansıması tamamen banka işlem süreci ile ilgili olduğundan, ALICI, olası gecikmeler için SATICI’nın herhangi bir şekilde müdahalede bulunmasının mümkün olamayacağını ve SATICI tarafından kredi kartına iade edilen tutarın banka tarafından ALICI hesabına yansıtılmasının ortalama 2 ile 3 haftayı bulabileceğini şimdiden kabul etmektedir.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\nMADDE 5 CAYMA HAKKI&lt;/p&gt;\r\n\r\n&lt;p&gt;ALICI; mal satışına ilişkin mesafeli sözleşmelerde, ürünün kendisine veya gösterdiği adresteki kişi/kuruluşa teslim tarihinden itibaren 14 (ondört) iş günü içerisinde hiçbir hukuki ve cezai sorumluluk üstlenmeksizin ve hiçbir gerekçe göstermeksizin malı reddederek sözleşmeden cayma hakkını kullanabilir. Hizmet sunumuna ilişkin mesafeli sözleşmelerde ise, bu süre sözleşmenin imzalandığı tarihte başlar. Cayma hakkı süresi sona ermeden önce, tüketicinin onayı ile hizmetin ifasına başlanan hizmet sözleşmelerinde cayma hakkı kullanılamaz. Cayma hakkının kullanımından kaynaklanan masraflar SATICI’ya aittir. Cayma hakkının kullanılması için 14 iş günlük süre içinde SATICI\'ya iadeli taahhütlü posta, faks veya e-posta ile yazılı bildirimde bulunulması ve ürünün 5-e madde hükümleri çerçevesinde kullanılmamış olması şarttır. Bu hakkın kullanılması halinde,&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;a) 3. kişiye veya ALICI’ya teslim edilen ürünün faturası, (İade edilmek istenen ürünün faturası kurumsal ise, geri iade ederken kurumun düzenlemiş olduğu iade faturası ile birlikte gönderilmesi gerekmektedir. Faturası kurumlar adına düzenlenen sipariş iadeleri İADE FATURASI kesilmediği takdirde tamamlanamayacaktır.)&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;b) Cayma hakkını kullanırken belirtilen dilekçe,&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;c) İade edilecek ürünlerin kutusu, ambalajı, varsa standart aksesuarları ile birlikte eksiksiz ve hasarsız olarak teslim edilmesi gerekmektedir.&lt;/p&gt;\r\n\r\n&lt;p&gt;d) SATICI, cayma bildiriminin kendisine ulaşmasında itibaren en geç 10 günlük süre içerisinde toplam bedeli ve alıcıyı borç altına sokan belgeleri ALICI’ya iade etmek ve 20 günlük süre içerisinde malı iade almakla yükümlüdür.&lt;/p&gt;\r\n\r\n&lt;p&gt;e) ALICI’nın kusurundan kaynaklanan bir nedenle malın değerinde bir azalma olursa veya iade imkânsızlaşırsa ALICI kusuru oranında SATICI’nın zararlarını tazmin etmekle yükümlüdür. Kişiye özel ölçü ve ürünler SATICI tarafından iade alınamaz. Hijyen gerektiren küpe vb ürünler SATICI tarafından iade alınamaz. Cayma hakkı kullanılmak istenen ürünler kullanılmamış olmalıdır. Bir defaya mahsus paketi açılan teknolojik ürünler SATICI tarafından iade alınamamaktadır.&lt;/p&gt;\r\n\r\n&lt;p&gt;f) Cayma hakkının kullanılması nedeniyle SATICI tarafından düzenlenen Alışveriş çeki kullanabilme tutarının altına düşülmesi halinde kampanya kapsamında faydalanılan Alışveriş çeki tutarı iptal edilir.&lt;/p&gt;\r\n\r\n&lt;p&gt;MADDE 6 &amp;nbsp;YETKİLİ MAHKEME&lt;/p&gt;\r\n\r\n&lt;p&gt;İşbu sözleşmenin uygulanmasında, Gümrük ve Ticaret Bakanlığınca ilan edilen değere kadar ALICI’nın mal veya hizmeti satın aldığı ve ikametgahının bulunduğu yerdeki Tüketici Hakem Heyetleri ile Tüketici Mahkemeleri yetkilidir. SATICI şikâyet ve itirazları konusunda başvurularını, T.C. Gümrük Ve Ticaret Bakanlığı tarafından her yıl Aralık ayında belirlenen parasal sınırlar dâhilinde mal veya hizmeti satın aldığı veya ikametgâhının bulunduğu yerdeki tüketici sorunları hakem heyetine veya tüketici mahkemesine yapabilir. Parasal sınıra ilişkin bilgiler aşağıdadır. 1 Ocak 2014 tarihinden itibaren geçerli olmak üzere 4077 sayılı Tüketicinin Korunması Hakkında Kanunun 22 nci maddesinin beşinci ve altıncı fıkralarındaki tüketici sorunları hakem heyetlerinin kararlarının bağlayıcı olacağına ilişkin üst veya tüketici mahkemelerinde delil olacağına ilişkin alt parasal sınır 1.272,19 TL, 1 Ağustos 2003 tarihli ve 25186 sayılı Resmî Gazete’de yayımlanan Tüketici Sorunları Hakem Heyetleri Yönetmeliğinin 5 inci maddesinin üçüncü fıkrasında, büyükşehir statüsünde bulunan illerde faaliyet gösteren il hakem heyetlerinin uyuşmazlıklara bakmakla görevli ve yetkili olmalarına ilişkin alt parasal sınır 3.321,17 TL olarak tespit edilmiştir.&lt;/p&gt;\r\n\r\n&lt;p&gt;İşbu Sözleşme ticari amaçlarla yapılmıştır.&lt;/p&gt;\r\n\r\n&lt;p&gt;SATICI : [firmaunvan]&lt;/p&gt;\r\n\r\n&lt;p&gt;ALICI :&lt;/p&gt;\r\n\r\n&lt;p&gt;TARİH&lt;/p&gt;', '', 4, 1, 0, 0),
                                                                                                                                                                                                  (7, 'SAJDM9EUFC78TZXK34P2', '2019-03-01 12:06:51.000000', '2020-09-24 10:49:14.000000', 15, 'Gizlilik İlkeleri', '&lt;p&gt;[firmaunvan]. (“[sitedomain]”) olarak, kullanıcılarımızın hizmetlerimizden güvenli ve eksiksiz şekilde faydalanmalarını sağlamak amacıyla sitemizi kullanan üyelerimizin gizliliğini korumak için çalışıyoruz. Bu doğrultuda, işbu [sitedomain] Gizlilik Politikas (“Politika”), üyelerimizin kişisel verilerinin 6698 sayılı Kişisel Verilerin Korunması Kanunu (“Kanun”) ile tamamen uyumlu bir şekilde işlenmesi ve kullanıcılarımızı bu bağlamda bilgilendirmek amacıyla hazırlanmıştır. [sitedomain].com çerez politikası İşbu Politika’nın ayrılmaz parçasıdır.&lt;br /&gt;\r\nİşbu Politika’nın amacı, [sitedomain] tarafından işletilmekte olan www.[sitedomain].com internet sitesi ile mobil uygulamanın (hepsi birlikte “Platform” olarak anılacaktır) işletilmesi sırasında Platform üyeleri/ziyaretçileri/kullanıcıları (hepsi birlikte “Veri Sahibi” olarak anılacaktır) tarafından [sitedomain] ile paylaşılan veya [sitedomain]’nın, Veri Sahibi’nin Platform’u kullanımı sırasında ürettiği kişisel verilerin kullanımına ilişkin koşul ve şartları tespit etmektir.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Hangi Veriler İşlenmektedir?&lt;/strong&gt;&lt;br /&gt;\r\nAşağıda [sitedomain] tarafından işlenen ve Kanun uyarınca kişisel veri sayılan verilerin hangileri olduğu sıralanmıştır. Aksi açıkça belirtilmedikçe, işbu Politika kapsamında arz edilen hüküm ve koşullar kapsamında “kişisel veri” ifadesi aşağıda yer alan bilgileri kapsayacaktır.&lt;br /&gt;\r\n• Kimlik Bilgisi&lt;br /&gt;\r\n• İletişim Bilgisi&lt;br /&gt;\r\n• Kullanıcı Bilgisi&lt;br /&gt;\r\n• Kullanıcı İşlem Bilgisi&lt;br /&gt;\r\n• İşlem Güvenliği Bilgisi&lt;br /&gt;\r\n• Finansal Bilgi&lt;br /&gt;\r\n• Pazarlama Bilgisi&lt;br /&gt;\r\n• Talep/Şikayet Yönetimi Bilgisi&lt;br /&gt;\r\nKişisel Verilerin Korunması Kanunu’nun 3. ve 7. maddeleri dairesince, geri döndürülemeyecek şekilde anonim hale getirilen veriler, anılan kanun hükümleri uyarınca kişisel veri olarak kabul edilmeyecek ve bu verilere ilişkin işleme faaliyetleri işbu Politika hükümleri ile bağlı olmaksızın gerçekleştirecektir.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Kişisel Veri İşleme Amaçları&lt;/strong&gt;&lt;br /&gt;\r\n[sitedomain], Veri Sahibi tarafından sağlanan kişisel verileri, üyelik kaydı ve hesabının oluşturulması ve buna ilişkin kayıtların tutulması, Veri Sahibi’nin Platform üzerinden sağlanan hizmetlerden faydalandırılması sistem hatalarının tespit edilerek performans takibinin yapılması ve Platform’un işleyişinin iyileştirilmesi, bakım ve destek hizmetleri ile yedekleme hizmetlerinin sunulması amaçları dahil olmak üzere [sitedomain] tarafından sunulan ürün ve hizmetlerden ilgili kişileri faydalandırmak için gerekli çalışmaların iş birimleri tarafından yapılması ve ilgili iş süreçlerinin yürütülmesi ile bu ürün ve hizmetlerin ilgili kişilerin beğeni, kullanım alışkanlıkları ve ihtiyaçlarına göre özelleştirilerek ilgili kişilere önerilmesi ve tanıtılması için gerekli olan aktivitelerin planlanması ve icrası, [sitedomain] tarafından yürütülen ticari faaliyetlerin gerçekleştirilmesi için ilgili iş birimleri tarafından gerekli çalışmaların yapılması ve buna bağlı iş süreçlerinin yürütülmesi, [sitedomain] ve iş ilişkisi içerisinde bulunduğu kişilerin hukuki, teknik ve ticari-iş güvenliğinin temini ile [sitedomain]’nın ticari ve/veya iş stratejilerinin planlanması ve icrası amaçlarıyla işlenebilecektir.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Veri Sahiplerinin Açık Rızası Doğrultusunda İşlenecek Kişisel Veriler ve İşleme Amaçları&lt;/strong&gt;&lt;br /&gt;\r\nVeri Sahibi’nin açık rızası kapsamında, [sitedomain], Veri Sahipleri’nin Platform üzerindeki hareketlerini takip ederek kullanıcı deneyiminin artırılması, istatistik oluşturulması, profilleme yapılması, doğrudan pazarlama ve yeniden pazarlama, Veri Sahibi’ne özel promosyon önerilerinin oluşturulması ve Veri Sahibi’ne iletilmesi ve bu kapsamda elde edilen verilerin her türlü reklam ve materyal içeriğinde kullanılması amacıyla veri işleyebilecek ve aşağıda anılan taraflarla bu verileri paylaşabilecektir.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Kişisel Verilerin Aktarımı:&lt;/strong&gt;&lt;br /&gt;\r\n[sitedomain], Veri Sahibi’ne ait kişisel verileri ve bu kişisel verileri kullanılarak elde ettiği yeni verileri, işbu Gizlilik Politikası ile belirlenen amaçların gerçekleştirilebilmesi için [sitedomain]’nın hizmetlerinden faydalandığı üçüncü kişilere, söz konusu hizmetlerin temini amacıyla sınırlı olmak üzere aktarılabilecektir. [sitedomain], Veri Sahibi deneyiminin geliştirilmesi (iyileştirme ve kişiselleştirme dâhil), Veri Sahibi’nin güvenliğini sağlamak, hileli ya da izinsiz kullanımları tespit etmek, operasyonel değerlendirme araştırılması, Platform hizmetlerine ilişkin hataların giderilmesi ve işbu Gizlilik Politikası’nda yer alan amaçlardan herhangi birisini gerçekleştirebilmek için SMS gönderimi yapanlar da dahil olmak üzere dış kaynak hizmet sağlayıcıları, barındırma hizmet sağlayıcıları (hosting servisleri), hukuk büroları, araştırma şirketleri, çağrı merkezleri gibi üçüncü kişiler ile paylaşabilecektir.&lt;/p&gt;\r\n\r\n&lt;p&gt;Kişisel veriler, Kanun’un 8. ve 9. maddelerinde belirtilen kişisel veri işleme şartları ve amaçları çerçevesinde [firmaunvan] ve [firmaunvan] Şirket yetkilileri, hissedarları, iş ortaklarımız, tedarikçilerimiz, kanunen yetkili kamu kurum ve kuruluşları ile kanunen yetkili özel kurumlar ile paylaşılabilecek, bu amaçlarla sınırlı olarak Kanun m.9’da işaret edilen usul esaslar ile Kişisel Verileri Koruma Kurulu kararları çerçevesinde yurt dışına aktarılabilecektir.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Kişisel Verilerin Toplanma Yöntemi ve Hukuki Sebebi&lt;/strong&gt;&lt;br /&gt;\r\nKişisel veriler, Platform üzerinden ve elektronik ortamda toplanmaktadır. Yukarıda belirtilen hukuki sebeplerle toplanan kişisel veriler 6698 sayılı Kanun’un 5. ve 6. maddelerinde ve bu Gizlilik Politikası’nda belirtilen amaçlarla işlenebilmekte ve aktarılabilmektedir.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Kişisel Veri Sahibinin Hakları&lt;/strong&gt;&lt;br /&gt;\r\nKanun’un 11. maddesi uyarınca veri sahipleri,&lt;br /&gt;\r\n• Kendileri ile ilgili kişisel veri işlenip işlenmediğini öğrenme, kişisel verileri işlenmişse buna ilişkin bilgi talep etme,&lt;br /&gt;\r\n• Kişisel verilerin işlenme amacını ve bunların amacına uygun kullanılıp kullanılmadığını öğrenme, yurt içinde veya yurt dışında kişisel verilerin aktarıldığı üçüncü kişileri bilme,&lt;br /&gt;\r\n• Kişisel verilerin eksik veya yanlış işlenmiş olması halinde bunların düzeltilmesini isteme ve bu kapsamda yapılan işlemin kişisel verilerin aktarıldığı üçüncü kişilere bildirilmesini isteme,&lt;br /&gt;\r\n• Kanun ve ilgili diğer kanun hükümlerine uygun olarak işlenmiş olmasına rağmen, işlenmesini gerektiren sebeplerin ortadan kalkması halinde kişisel verilerin silinmesini veya yok edilmesini isteme ve bu kapsamda yapılan işlemin kişisel verilerin aktarıldığı üçüncü kişilere bildirilmesini isteme,&lt;br /&gt;\r\n• İşlenen verilerin münhasıran otomatik sistemler vasıtasıyla analiz edilmesi suretiyle kişinin kendisi aleyhine bir sonucun ortaya çıkmasına itiraz etme ve kişisel verilerin kanuna aykırı olarak işlenmesi sebebiyle zarara uğraması halinde zararın giderilmesini talep etme haklarına sahiptir.&lt;br /&gt;\r\nSöz konusu hakların kullanımına ilişkin talepler, kişisel veri sahipleri tarafından www.[sitedomain].com adresinde yer alan 6698 sayılı Kanun Kapsamında [sitedomain] tarafından hazırlanan Kişisel Verilerin İşlenmesi ve Korunmasına ilişkin Politika’da belirtilen yöntemlerle iletilebilecektir. [sitedomain], söz konusu talepleri otuz gün içerisinde sonuçlandıracaktır. [sitedomain]’nın taleplere ilişkin olarak Kişisel Verileri Koruma Kurulu tarafından belirlenen (varsa) ücret tarifesi üzerinden ücret talep etme hakkı saklıdır.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Hertürlü sorunuz için; &lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;[firmaeposta]&lt;/p&gt;\r\n\r\n&lt;p&gt;[sitedomain]&lt;/p&gt;', '', 5, 1, 0, 0),
