                                                                                                                                                                                                  (9, '5loxGkGCAkrrNM5REn5u', '2019-06-09 00:00:00.000000', '2024-06-24 00:03:36.000000', 1, 'İletişim', '<p></p><div class=\"contactInfo\">\r\n[firmaad]\r\n[telefon]\r\n[mail]\r\n[whatsapp]\r\n[adres]\r\n</div>\r\n[iletisimform]\r\n\r\n[sosyalmedya]\r\n\r\n[firmaharita]', '/iletisim', 7, 1, 0, 0),
