function getLocationData(action,locationID,targetSelect){if(action==""){return}if(action=="getCity"&&locationID!=212){return}$.ajax({url:"/App/Controller/Admin/AdminLocationController.php",type:"POST",data:{action:action,id:locationID},success:function(data){targetSelect.empty();var data=JSON.parse(data);if(data.status=="success"){var locations=data.location;var option='<option value="">Seçiniz</option>';targetSelect.append(option);for(var i=0;i<locations.length;i++){var location=locations[i];var locationID=location.id;var locationName=location.name;option='<option value="'+locationID+'">'+locationName+"</option>";targetSelect.append(option)}}},error:function(jqXHR,textStatus,errorThrown){console.error(textStatus,errorThrown)}})}function convertInputSelect(thisContainer,selectNames,countryID){for(var i=1;i<selectNames.length;i++){var selectName=selectNames[i];if(countryID!=="212"){var select=thisContainer.find("select[name='"+selectName+"']");select.replaceWith('<input type="text" class="form-control" name="'+selectName+'" id="'+selectName+'">')}else{var select=thisContainer.find("input[name='"+selectName+"']");if(select.length>0){select.replaceWith('<select class="form-control" name="'+selectName+'" id="'+selectName+'"><option value="">Seçiniz</option></select>')}}}}function getPostalCode(locationID,target,callback){console.log(target);$.ajax({url:"/App/Controller/Admin/AdminLocationController.php",type:"POST",data:{action:"getPostalCode",id:locationID},success:function(data){var data=JSON.parse(data);if(data.status=="success"){var postalCode=data.postalCode;var targetInput=$("#"+target);var targetValue=targetInput.val();var pattern=/PK: \d{5}/;var match=targetValue.match(pattern);if(match!==null){targetValue=targetValue.replace(pattern,"PK: "+postalCode)}else{targetValue+=" PK: "+postalCode}targetInput.val(targetValue);if(typeof callback==="function"){callback(postalCode)}}else{if(typeof callback==="function"){callback("")}}},error:function(jqXHR,textStatus,errorThrown){console.error(textStatus,errorThrown);if(typeof callback==="function"){callback("")}}})}